import { Router } from "express";
import { errorHandler } from "../middlewares/errorHandler.js";
import isAdmin from "../middlewares/isAdmin.js";
import adminHighlights from "../controllers/report/adminHighlights.js";
import adminChart from "../controllers/report/adminChart.js";
import isSale from "../middlewares/isSale.js";
import salesHighlights from "../controllers/report/salesHightlights.js";
import salesChart from "../controllers/report/salesChart.js";
const reportRouter = Router();
reportRouter.get('/adminHighlights', errorHandler(isAdmin), errorHandler(adminHighlights));
reportRouter.get('/adminChart', errorHandler(isAdmin), errorHandler(adminChart));
reportRouter.get('/salesHighlights', errorHandler(isSale), errorHandler(salesHighlights));
reportRouter.get('/salesChart', errorHandler(isSale), errorHandler(salesChart));
export default reportRouter;
